/**
                      esthesis-normalise-data.js
                      NiFi ScriptedRecordSetWriter to normalise incoming telemetry/metadata.

                      Incoming records format:
                      [
                      {
                      "m": "test",
                      "v": 123
                      },
                      {
                      "m": "test",
                      "v": {
                      "temperature": 32,
                      "humidity": 33
                      }
                      }
                      ]

                      Outgoing records format:
                      [
                      {
                      "measurement": "test",
                      "timestamp": 1569483969551,
                      "fields": {
                      "value": 123
                      },
                      "tags": {
                      "hardwareId": "device1",
                      "type": "telemetry"
                      }
                      },
                      {
                      "measurement": "test",
                      "timestamp": 1569483969564,
                      "fields": {
                      "temperature": 32,
                      "humidity": 33
                      },
                      "tags": {
                      "hardwareId": "device1",
                      "type": "telemetry"
                      }
                      }
                      ]

                      Tags are derived from Flow File attributes resolved in previous steps.
                      If incoming record is a single object it will still be wrapped into an array.
                      **/

                      // Get references the the output stream to write results, the input stream to
                      //read the content of the FlowFile, and to the incoming FlowFile.
                      var InputStreamCallback =
                      Java.type("org.apache.nifi.processor.io.InputStreamCallback");
                      var OutputStreamCallback =
                      Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                      var IOUtils = Java.type("org.apache.commons.io.IOUtils");
                      var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");
                      var flowFile = session.get();

                      // If no FlowFile exists transition to the failure relationship, otherwise
                      //proceed with processing.
                      if (flowFile != null) {
                      var dataPoints = [];

                      // Read incoming FlowFile and convert data to records.
                      session.read(flowFile, new InputStreamCallback(function (inputStream) {
                      // Parse incoming FlowFile content to JSON.
                      var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
                      if (!text.startsWith('[')) {
                      text = "[" + text + "]";
                      }
                      var json = JSON.parse(text);

                      // Iterate through records.
                      for (var i in json) {
                      var jsonEntry = json[i];
                      var dataPoint = {};

                      // Check if time exists and add it if missing.
                      if (!jsonEntry.t) {
                      jsonEntry.t = flowFile.getAttribute('esthesis.nifi.timestamp');
                      }

                      // Check whether a single or multiple values are received and create output.
                      dataPoint['measurement'] = jsonEntry.m;
                      dataPoint['timestamp'] = jsonEntry.t;
                      var fields = {};
                      if (typeof jsonEntry.v === 'object') {
                      for (var j in jsonEntry.v) {
                      fields[j] = jsonEntry.v[j];
                      }
                      } else {
                      fields['value'] = jsonEntry.v;
                      }
                      dataPoint['fields'] = fields;

                      // Add tags.
                      dataPoint['tags'] = {
                      'hardwareId': flowFile.getAttribute('esthesis.hardwareId'),
                      'type': flowFile.getAttribute('esthesis.type')
                      };

                      // Push record.
                      dataPoints.push(dataPoint);
                      }
                      }));

                      // Replace FlowFile's content with the data in InfluxDB line protocol.
                      flowFile = session.write(flowFile, new OutputStreamCallback(function
                      (outputStream) {
                      outputStream.write(JSON.stringify(dataPoints).getBytes(StandardCharsets.UTF_8));
                      }));
                      session.transfer(flowFile, REL_SUCCESS);
                      }

/*
                              * esthesis-influxdb-results-transformer.js
                              * NiFi ExecuteScript to convert InfluxDB data to esthesis schema:
                              *
                              * {"measurement": [
                              *  {"time": , "field1": , "field2":, ...
                              * ]}
                              */

                              function parseToMilis(date) {
                              var dotPosition = date.indexOf(".");
                              var timezonePosition = date.length -1;
                              var nanos = date.substring(dotPosition - 1, timezonePosition);
                              var milis = (parseFloat(nanos).toFixed(3) + date.charAt(timezonePosition));
                              return date.substring(0, dotPosition - 1) + milis;
                              }

                              // Get references the the output stream to write results, the input stream to read the content of
                              // the FlowFile, and to the incoming FlowFile.
                              var InputStreamCallback = Java.type("org.apache.nifi.processor.io.InputStreamCallback")
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var IOUtils = Java.type("org.apache.commons.io.IOUtils")
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets")
                              var flowFile = session.get();
                              var nonTimeOperations = ['count', 'mean', 'sum'];

                              if (flowFile != null) {
                              var operation = flowFile.getAttribute('esthesis.operation');
                              try {
                              var output = {};
                              session.read(flowFile, new InputStreamCallback(function (inputStream) {
                              // Parse incoming FlowFile content to JSON.
                              var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
                              var json = JSON.parse(text);

                              if (json.length > 0) {
                              for (i in json) {
                              var series = json[i].results[0].series[0];
                              var columns = series.columns;
                              // Set the root-level key as the name of the measurement.
                              if (!output[series.name]) {
                              output[series.name] = [];
                              }

                              // Fill-in each value from the result set.
                              for (j in series.values) {
                              val = {};
                              for (key in series.values[j]) {
                              if (columns[key] === 'time') {
                              if (nonTimeOperations.indexOf(operation) == -1) {
                              val['timestamp'] = Date.parse(parseToMilis(series.values[j][key]));
                              }
                              } else {
                              // Ignore null values.
                              if (series.values[j][key]) {
                              val[columns[key]] = series.values[j][key];
                              }
                              }
                              if (columns.indexOf('type') == -1) {
                              val['type'] = flowFile.getAttribute('esthesis.type');
                              }
                              }
                              output[series.name].push(val);
                              }
                              }
                              }

                              // Convert content.
                              if (json.results && json.results[0] && json.results[0].series && json.results[0].series[0]) {
                              var series = json.results[0].series[0];
                              var columns = series.columns;
                              // Set the root-level key as the name of the measurement.
                              output[series.name] = [];

                              // Fill-in each value from the result set.
                              for (i in series.values) {
                              val = {};
                              for (key in series.values[i]) {
                              if (columns[key] === 'time') {
                              if (nonTimeOperations.indexOf(operation) == -1) {
                              val['timestamp'] = Date.parse(parseToMilis(series.values[i][key]));
                              }
                              } else {
                              // Ignore null values.
                              if (series.values[i][key]) {
                              val[columns[key]] = series.values[i][key];
                              }
                              }

                              if (columns.indexOf('type') == -1) {
                              val['type'] = flowFile.getAttribute('esthesis.type');
                              }
                              }
                              output[series.name].push(val);
                              }
                              }
                              }));

                              // Replace FlowFile's content with the data in InfluxDB line protocol.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(JSON.stringify(output).getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

/*
                              * esthesis-influxdb-create-query.js
                              * NiFi ExecuteScript to convert incoming telemetry/metadata read requests to an InfluxDB query.
                              *
                              * All incoming parameters are expected to be already sanitised.
                              */
                              var allowedOperations = ['query', 'count', 'max', 'min', 'mean', 'sum'];

                              /**
                              * Utility method to set the fields of a query.
                              * @param flowFile The FlowFile to get the fields from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the fields value.
                              * @private
                              */
                              function _updateFields(flowFile, queryTemplate) {
                              var fields = flowFile.getAttribute('esthesis.param.fields').trim();
                              if (!fields) {
                              fields = "*";
                              }
                              return queryTemplate.replace("$FIELDS", fields);
                              }

                              /**
                              * Utility method to set the measurement of a query.
                              * @param flowFile The FlowFile to get the measurement from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the measurement value.
                              * @private
                              */
                              function _updateMeasurement(flowFile, queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              if (!measurement) {
                              throw  "Measurement parameter can not be empty.";
                              //session.transfer(flowFile, REL_FAILURE);
                              }
                              return queryTemplate.replace("$MEASUREMENT", measurement);
                              }

                              /**
                              * Utility method to set the tags of a query.
                              * @param flowFile The FlowFile to get the tags from.
                              * @param queryTemplate The query template in which tags are updated.
                              * @returns {void | string} The original query template updated with the tags value.
                              * @private
                              */
                              function _updateTags(flowFile, queryTemplate) {
                              var hardwareId = flowFile.getAttribute('esthesis.hardwareId');

                              var hardwareIdQuery;
                              if ((hardwareId.startsWith('/') && hardwareId.endsWith('/'))) {
                              hardwareIdQuery = "hardwareId =~ " + hardwareId;
                              } else {
                              hardwareIdQuery = "hardwareId = '" + hardwareId + "'";
                              }

                              return queryTemplate.replace("$TAGS",
                              hardwareIdQuery + " and type = '" + flowFile.getAttribute('esthesis.type') + "'");
                              }

                              function getCalculateFields(flowFile, operation) {
                              // Create the fields template (i.e. first part of the SELECT query).
                              var fieldsTemplate;
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              if (!fields) {
                              fieldsTemplate = operation + "(*)";
                              } else {
                              fieldsTemplate = operation + "(" + fields + ") as " +  operation + "_" + fields;
                              }

                              return fieldsTemplate;
                              }

                              /**
                              * Create a generic query request.
                              * @param flowFile The FlowFile to process information to create the request.
                              * @param fields A list of fields already resolved in a previous step.
                              */
                              function createQueryRequest(flowFile, fields) {
                              // Set the template for this type of execution.
                              var queryTemplate = "SELECT $FIELDS FROM $MEASUREMENT WHERE $TAGS $TIME ORDER BY time $ORDER $PAGING";

                              // Set fields, measurement and tags.
                              if (fields) {
                              queryTemplate = queryTemplate.replace("$FIELDS", fields);
                              }
                              else {
                              queryTemplate = _updateFields(flowFile, queryTemplate);
                              }
                              queryTemplate = _updateMeasurement(flowFile, queryTemplate);
                              queryTemplate = _updateTags(flowFile, queryTemplate);

                              // Set time (incoming time is in msec, so it needs to be converted to nanoseconds for InfluxDB).
                              var timeFrom = flowFile.getAttribute('esthesis.param.from');
                              var timeTo = flowFile.getAttribute('esthesis.param.to');
                              if (timeFrom && timeTo) {
                              timeFrom = timeFrom * 1000000;
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME",
                              "and time >= " + timeFrom + " and time <= " + timeTo);
                              } else if (timeFrom) {
                              timeFrom = timeFrom * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and time >= " + timeFrom);
                              } else if (timeTo) {
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and time <= " + timeTo);
                              } else {
                              queryTemplate = queryTemplate.replace("$TIME", "");
                              }

                              // Set order.
                              var order = flowFile.getAttribute('esthesis.param.order');
                              if (order) {
                              order = order.toLowerCase();
                              if ((order === 'asc' || order === 'desc')) {
                              queryTemplate = queryTemplate.replace("$ORDER", order);
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              } else if (isMetadataQuery) {
                              queryTemplate = queryTemplate.replace("$ORDER", "desc");
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }

                              // Set paging.
                              var limit = isMetadataQuery ? 1 : flowFile.getAttribute(
                              'http.query.param.limit');
                              var offset = flowFile.getAttribute('http.query.param.offset');
                              if (limit && offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit + " OFFSET " + offset);
                              } else if (limit) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit);
                              } else if (offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "OFFSET " + offset);
                              } else {
                              queryTemplate = queryTemplate.replace("$PAGING", "");
                              }

                              log.info("Created InfluxDB query: " + queryTemplate);
                              return queryTemplate;
                              }

                              // Get references the the output stream to write results and to the incoming FlowFile.
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");
                              var flowFile = session.get();
                              var isMetadataQuery = flowFile.getAttribute('esthesis.type') === "metadata";

                              // If FlowFile exists proceed with processing.
                              if (flowFile != null) {
                              try {
                              // Define the query to be executed.
                              var queryTemplate;

                              // Call the appropriate handler for the the type of query requested.
                              var operation = flowFile.getAttribute('esthesis.operation').trim().toLowerCase();
                              var fields = flowFile.getAttribute('esthesis.param.fields');

                              if (isMetadataQuery && operation !== 'query' ) {
                              throw('Requested operation \'' + operation + "\' is not supported for metadata");
                              }

                              if (allowedOperations.indexOf(operation) > -1) {
                              queryTemplate = operation === 'query' ? createQueryRequest(flowFile) : createQueryRequest(
                              flowFile, getCalculateFields(flowFile, operation));
                              } else {
                              throw('Requested operation \'' + operation + "\' is not supported.");
                              }

                              // Replace FlowFile's content with the query to be executed.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(queryTemplate.getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

/*
                              * esthesis-mysql-create-query.js
                              * NiFi ExecuteScript to convert incoming telemetry/metadata read requests to a MySQL query.
                              *
                              * All incoming parameters are expected to be already sanitised.
                              */

                              // The list of allowed operations on data.
                              // 'mean' is converted to 'avg' for MySQL.
                              var allowedOperations = ['query', 'count', 'max', 'min', 'mean', 'sum'];
                              var nonTimeOperations = ['count', 'mean', 'sum'];

                              /**
                              * Utility method to set the fields of a query.
                              * @param flowFile The FlowFile to get the fields from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the fields value.
                              * @private
                              */
                              function _updateFields(flowFile, queryTemplate) {
                              var fields = flowFile.getAttribute('esthesis.param.fields').trim();
                              if (!fields) {
                              queryTemplate = queryTemplate.replace("$GROUPBY", "");
                              return queryTemplate.replace("$FIELDS", "*");
                              } else {
                              queryTemplate = queryTemplate.replace("$GROUPBY", "GROUP BY " + fields + ", timestamp");
                              return queryTemplate.replace("$FIELDS", fields + ", timestamp");
                              }
                              }

                              /**
                              * Utility method to set the measurement of a query.
                              * @param flowFile The FlowFile to get the measurement from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the measurement value.
                              * @private
                              */
                              function _updateMeasurement(flowFile, queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              if (!measurement) {
                              throw  "Measurement parameter can not be empty.";
                              //session.transfer(flowFile, REL_FAILURE);
                              }
                              return queryTemplate.replace("$MEASUREMENT", measurement);
                              }

                              /**
                              * Utility method to set the tags of a query.
                              * @param flowFile The FlowFile to get the tags from.
                              * @param queryTemplate The query template in which tags are updated.
                              * @returns {void | string} The original query template updated with the tags value.
                              * @private
                              */
                              function _updateTags(flowFile, queryTemplate) {
                              var hardwareId = flowFile.getAttribute('esthesis.hardwareId');

                              var hardwareIdQuery;
                              if ((hardwareId.startsWith('/') && hardwareId.endsWith('/'))) {
                              hardwareIdQuery = "hardwareId REGEXP " + hardwareId;
                              } else {
                              hardwareIdQuery = "hardwareId = '" + hardwareId + "'";
                              }

                              return queryTemplate.replace("$TAGS", hardwareIdQuery);
                              }

                              function getCalculateFields(flowFile, operation) {
                              // Create the fields template (i.e. first part of the SELECT query).
                              var fieldsTemplate;
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              fieldsTemplate = operation + "(" + fields + ") as " + (operation === "avg" ? "mean" : operation)
                              + "_" + fields;

                              return fieldsTemplate;
                              }

                              function _updateTime(flowFile, queryTemplate) {
                              // Set time (incoming time is in msec, so it needs to be converted to nanoseconds for InfluxDB).
                              var timeFrom = flowFile.getAttribute('esthesis.param.from');
                              var timeTo = flowFile.getAttribute('esthesis.param.to');
                              if (timeFrom && timeTo) {
                              timeFrom = timeFrom * 1000000;
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME",
                              "and timestamp >= " + timeFrom + " and timestamp <= " + timeTo);
                              } else if (timeFrom) {
                              timeFrom = timeFrom * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and timestamp >= " + timeFrom);
                              } else if (timeTo) {
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and timestamp <= " + timeTo);
                              } else {
                              queryTemplate = queryTemplate.replace("$TIME", "");
                              }
                              return queryTemplate;
                              }

                              function _updateOrder(flowFile, queryTemplate) {
                              // Set order.
                              var order = flowFile.getAttribute('esthesis.param.order');
                              if (order) {
                              order = order.toLowerCase();
                              if ((order === 'asc' || order === 'desc')) {
                              queryTemplate = queryTemplate.replace("$ORDER", order);
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              return queryTemplate;
                              }

                              function addTimestampToOperation(queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              return "SELECT " + fields +
                              " as " + operation + "_" + fields + ", timestamp from " + measurement + " WHERE "
                              + fields + " in " + "(" + queryTemplate + ")" + " ORDER BY  timestamp desc LIMIT 1;";
                              }

                              /**
                              * Create a generic query request.
                              * @param flowFile The FlowFile to process information to create the request.
                              * @param fields A list of fields already resolved in a previous step.
                              */
                              function createQueryRequest(flowFile, fields) {
                              // Set the template for this type of execution.
                              var queryTemplate = "SELECT $FIELDS FROM $MEASUREMENT WHERE $TAGS $TIME $GROUPBY ORDER BY timestamp $ORDER $PAGING";

                              if (nonTimeOperations.indexOf(operation) == -1) {
                              queryTemplate = queryTemplate.replace("ORDER BY timestamp $ORDER", "");
                              }

                              // Set fields, measurement and tags.
                              if (fields) {
                              queryTemplate = queryTemplate.replace("$FIELDS", fields);
                              queryTemplate = queryTemplate.replace("$GROUPBY", "");
                              } else {
                              queryTemplate = _updateFields(flowFile, queryTemplate);
                              }

                              queryTemplate = _updateMeasurement(flowFile, queryTemplate);
                              queryTemplate = _updateTags(flowFile, queryTemplate);
                              queryTemplate = _updateTime(flowFile, queryTemplate);
                              queryTemplate = _updateOrder(flowFile, queryTemplate);

                              // Set paging.
                              var limit = flowFile.getAttribute('http.query.param.limit');
                              var offset = flowFile.getAttribute('http.query.param.offset');
                              if (limit && offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit + " OFFSET " + offset);
                              } else if (limit) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit);
                              } else if (offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "OFFSET " + offset);
                              } else {
                              queryTemplate = queryTemplate.replace("$PAGING", "");
                              }

                              log.trace("Created MYSQL query: " + queryTemplate);

                              if (operation === 'max' || operation === 'min') {
                              queryTemplate = addTimestampToOperation(queryTemplate);
                              }
                              return queryTemplate;
                              }

                              // Get references the the output stream to write results and to the incoming FlowFile.
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");
                              var flowFile = session.get();
                              var isMetadataQuery = flowFile.getAttribute('esthesis.type') === "metadata";

                              // If FlowFile exists proceed with processing.
                              if (flowFile != null) {
                              try {
                              // Define the query to be executed.
                              var queryTemplate;

                              // Call the appropriate handler for the the type of query requested.
                              var operation = flowFile.getAttribute('esthesis.operation').trim().toLowerCase();
                              if (isMetadataQuery && operation !== 'query' ) {
                              throw('Requested operation \'' + operation + "\' is not supported for metadata");
                              }
                              if (allowedOperations.indexOf(operation) > -1) {
                              if (operation === 'query') {
                              queryTemplate = createQueryRequest(flowFile);
                              } else {
                              if (operation == "mean") {
                              operation = "avg";
                              }
                              queryTemplate = createQueryRequest(flowFile, getCalculateFields(flowFile, operation));
                              }
                              } else {
                              throw('Requested operation \'' + operation + "\' is not supported.");
                              }

                              // Replace FlowFile's content with the query to be executed.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(queryTemplate.getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

/*
                              * esthesis-mysql-create-query.js
                              * NiFi ExecuteScript to convert incoming telemetry/metadata read requests to a MySQL query.
                              *
                              * All incoming parameters are expected to be already sanitised.
                              */

                              // The list of allowed operations on data.
                              // 'mean' is converted to 'avg' for MySQL.
                              var allowedOperations = ['query', 'count', 'max', 'min', 'mean', 'sum'];
                              var nonTimeOperations = ['count', 'mean', 'sum'];

                              /**
                              * Utility method to set the fields of a query.
                              * @param flowFile The FlowFile to get the fields from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the fields value.
                              * @private
                              */
                              function _updateFields(flowFile, queryTemplate) {
                              var fields = flowFile.getAttribute('esthesis.param.fields').trim();
                              if (!fields) {
                              queryTemplate = queryTemplate.replace("$GROUPBY", "");
                              return queryTemplate.replace("$FIELDS", "*");
                              } else {
                              queryTemplate = queryTemplate.replace("$GROUPBY", "GROUP BY " + fields + ", timestamp");
                              return queryTemplate.replace("$FIELDS", fields + ", timestamp");
                              }
                              }

                              /**
                              * Utility method to set the measurement of a query.
                              * @param flowFile The FlowFile to get the measurement from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the measurement value.
                              * @private
                              */
                              function _updateMeasurement(flowFile, queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              if (!measurement) {
                              throw  "Measurement parameter can not be empty.";
                              //session.transfer(flowFile, REL_FAILURE);
                              }
                              return queryTemplate.replace("$MEASUREMENT", measurement);
                              }

                              /**
                              * Utility method to set the tags of a query.
                              * @param flowFile The FlowFile to get the tags from.
                              * @param queryTemplate The query template in which tags are updated.
                              * @returns {void | string} The original query template updated with the tags value.
                              * @private
                              */
                              function _updateTags(flowFile, queryTemplate) {
                              var hardwareId = flowFile.getAttribute('esthesis.hardwareId');

                              var hardwareIdQuery;
                              if ((hardwareId.startsWith('/') && hardwareId.endsWith('/'))) {
                              hardwareIdQuery = "hardwareId REGEXP " + hardwareId;
                              } else {
                              hardwareIdQuery = "hardwareId = '" + hardwareId + "'";
                              }

                              return queryTemplate.replace("$TAGS", hardwareIdQuery);
                              }

                              function getCalculateFields(flowFile, operation) {
                              // Create the fields template (i.e. first part of the SELECT query).
                              var fieldsTemplate;
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              fieldsTemplate = operation + "(" + fields + ") as " + (operation === "avg" ? "mean" : operation)
                              + "_" + fields;

                              return fieldsTemplate;
                              }

                              function _updateTime(flowFile, queryTemplate) {
                              // Set time (incoming time is in msec, so it needs to be converted to nanoseconds for InfluxDB).
                              var timeFrom = flowFile.getAttribute('esthesis.param.from');
                              var timeTo = flowFile.getAttribute('esthesis.param.to');
                              if (timeFrom && timeTo) {
                              timeFrom = timeFrom * 1000000;
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME",
                              "and timestamp >= " + timeFrom + " and timestamp <= " + timeTo);
                              } else if (timeFrom) {
                              timeFrom = timeFrom * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and timestamp >= " + timeFrom);
                              } else if (timeTo) {
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and timestamp <= " + timeTo);
                              } else {
                              queryTemplate = queryTemplate.replace("$TIME", "");
                              }
                              return queryTemplate;
                              }

                              function _updateOrder(flowFile, queryTemplate) {
                              // Set order.
                              var order = flowFile.getAttribute('esthesis.param.order');
                              if (order) {
                              order = order.toLowerCase();
                              if ((order === 'asc' || order === 'desc')) {
                              queryTemplate = queryTemplate.replace("$ORDER", order);
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              return queryTemplate;
                              }

                              function addTimestampToOperation(queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              return "SELECT " + fields +
                              " as " + operation + "_" + fields + ", timestamp from " + measurement + " WHERE "
                              + fields + " in " + "(" + queryTemplate + ")" + " ORDER BY  timestamp desc LIMIT 1;";
                              }

                              /**
                              * Create a generic query request.
                              * @param flowFile The FlowFile to process information to create the request.
                              * @param fields A list of fields already resolved in a previous step.
                              */
                              function createQueryRequest(flowFile, fields) {
                              // Set the template for this type of execution.
                              var queryTemplate = "SELECT $FIELDS FROM $MEASUREMENT WHERE $TAGS $TIME $GROUPBY ORDER BY timestamp $ORDER $PAGING";

                              if (nonTimeOperations.indexOf(operation) == -1) {
                              queryTemplate = queryTemplate.replace("ORDER BY timestamp $ORDER", "");
                              }

                              // Set fields, measurement and tags.
                              if (fields) {
                              queryTemplate = queryTemplate.replace("$FIELDS", fields);
                              queryTemplate = queryTemplate.replace("$GROUPBY", "");
                              } else {
                              queryTemplate = _updateFields(flowFile, queryTemplate);
                              }

                              queryTemplate = _updateMeasurement(flowFile, queryTemplate);
                              queryTemplate = _updateTags(flowFile, queryTemplate);
                              queryTemplate = _updateTime(flowFile, queryTemplate);
                              queryTemplate = _updateOrder(flowFile, queryTemplate);

                              // Set paging.
                              var limit = flowFile.getAttribute('http.query.param.limit');
                              var offset = flowFile.getAttribute('http.query.param.offset');
                              if (limit && offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit + " OFFSET " + offset);
                              } else if (limit) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit);
                              } else if (offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "OFFSET " + offset);
                              } else {
                              queryTemplate = queryTemplate.replace("$PAGING", "");
                              }

                              log.trace("Created MYSQL query: " + queryTemplate);

                              if (operation === 'max' || operation === 'min') {
                              queryTemplate = addTimestampToOperation(queryTemplate);
                              }
                              return queryTemplate;
                              }

                              // Get references the the output stream to write results and to the incoming FlowFile.
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");
                              var flowFile = session.get();
                              var isMetadataQuery = flowFile.getAttribute('esthesis.type') === "metadata";

                              // If FlowFile exists proceed with processing.
                              if (flowFile != null) {
                              try {
                              // Define the query to be executed.
                              var queryTemplate;

                              // Call the appropriate handler for the the type of query requested.
                              var operation = flowFile.getAttribute('esthesis.operation').trim().toLowerCase();
                              if (isMetadataQuery && operation !== 'query' ) {
                              throw('Requested operation \'' + operation + "\' is not supported for metadata");
                              }
                              if (allowedOperations.indexOf(operation) > -1) {
                              if (operation === 'query') {
                              queryTemplate = createQueryRequest(flowFile);
                              } else {
                              if (operation == "mean") {
                              operation = "avg";
                              }
                              queryTemplate = createQueryRequest(flowFile, getCalculateFields(flowFile, operation));
                              }
                              } else {
                              throw('Requested operation \'' + operation + "\' is not supported.");
                              }

                              // Replace FlowFile's content with the query to be executed.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(queryTemplate.getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

/*
                              * esthesis-influxdb-results-transformer.js
                              * NiFi ExecuteScript to convert InfluxDB data to esthesis schema:
                              *
                              * {"measurement": [
                              *  {"time": , "field1": , "field2":, ...
                              * ]}
                              */

                              function parseToMilis(date) {
                              var dotPosition = date.indexOf(".");
                              var timezonePosition = date.length -1;
                              var nanos = date.substring(dotPosition - 1, timezonePosition);
                              var milis = (parseFloat(nanos).toFixed(3) + date.charAt(timezonePosition));
                              return date.substring(0, dotPosition - 1) + milis;
                              }

                              // Get references the the output stream to write results, the input stream to read the content of
                              // the FlowFile, and to the incoming FlowFile.
                              var InputStreamCallback = Java.type("org.apache.nifi.processor.io.InputStreamCallback")
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var IOUtils = Java.type("org.apache.commons.io.IOUtils")
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets")
                              var flowFile = session.get();
                              var nonTimeOperations = ['count', 'mean', 'sum'];

                              if (flowFile != null) {
                              var operation = flowFile.getAttribute('esthesis.operation');
                              try {
                              var output = {};
                              session.read(flowFile, new InputStreamCallback(function (inputStream) {
                              // Parse incoming FlowFile content to JSON.
                              var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
                              var json = JSON.parse(text);

                              if (json.length > 0) {
                              for (i in json) {
                              var series = json[i].results[0].series[0];
                              var columns = series.columns;
                              // Set the root-level key as the name of the measurement.
                              if (!output[series.name]) {
                              output[series.name] = [];
                              }

                              // Fill-in each value from the result set.
                              for (j in series.values) {
                              val = {};
                              for (key in series.values[j]) {
                              if (columns[key] === 'time') {
                              if (nonTimeOperations.indexOf(operation) == -1) {
                              val['timestamp'] = Date.parse(parseToMilis(series.values[j][key]));
                              }
                              } else {
                              // Ignore null values.
                              if (series.values[j][key]) {
                              val[columns[key]] = series.values[j][key];
                              }
                              }
                              if (columns.indexOf('type') == -1) {
                              val['type'] = flowFile.getAttribute('esthesis.type');
                              }
                              }
                              output[series.name].push(val);
                              }
                              }
                              }

                              // Convert content.
                              if (json.results && json.results[0] && json.results[0].series && json.results[0].series[0]) {
                              var series = json.results[0].series[0];
                              var columns = series.columns;
                              // Set the root-level key as the name of the measurement.
                              output[series.name] = [];

                              // Fill-in each value from the result set.
                              for (i in series.values) {
                              val = {};
                              for (key in series.values[i]) {
                              if (columns[key] === 'time') {
                              if (nonTimeOperations.indexOf(operation) == -1) {
                              val['timestamp'] = Date.parse(parseToMilis(series.values[i][key]));
                              }
                              } else {
                              // Ignore null values.
                              if (series.values[i][key]) {
                              val[columns[key]] = series.values[i][key];
                              }
                              }

                              if (columns.indexOf('type') == -1) {
                              val['type'] = flowFile.getAttribute('esthesis.type');
                              }
                              }
                              output[series.name].push(val);
                              }
                              }
                              }));

                              // Replace FlowFile's content with the data in InfluxDB line protocol.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(JSON.stringify(output).getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

/*
                              * esthesis-influxdb-create-query.js
                              * NiFi ExecuteScript to convert incoming telemetry/metadata read requests to an InfluxDB query.
                              *
                              * All incoming parameters are expected to be already sanitised.
                              */
                              var allowedOperations = ['query', 'count', 'max', 'min', 'mean', 'sum'];

                              /**
                              * Utility method to set the fields of a query.
                              * @param flowFile The FlowFile to get the fields from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the fields value.
                              * @private
                              */
                              function _updateFields(flowFile, queryTemplate) {
                              var fields = flowFile.getAttribute('esthesis.param.fields').trim();
                              if (!fields) {
                              fields = "*";
                              }
                              return queryTemplate.replace("$FIELDS", fields);
                              }

                              /**
                              * Utility method to set the measurement of a query.
                              * @param flowFile The FlowFile to get the measurement from.
                              * @param queryTemplate The query template in which fields are updated.
                              * @returns {void | string} The original query template updated with the measurement value.
                              * @private
                              */
                              function _updateMeasurement(flowFile, queryTemplate) {
                              var measurement = flowFile.getAttribute('esthesis.param.measurement');
                              if (!measurement) {
                              throw  "Measurement parameter can not be empty.";
                              //session.transfer(flowFile, REL_FAILURE);
                              }
                              return queryTemplate.replace("$MEASUREMENT", measurement);
                              }

                              /**
                              * Utility method to set the tags of a query.
                              * @param flowFile The FlowFile to get the tags from.
                              * @param queryTemplate The query template in which tags are updated.
                              * @returns {void | string} The original query template updated with the tags value.
                              * @private
                              */
                              function _updateTags(flowFile, queryTemplate) {
                              var hardwareId = flowFile.getAttribute('esthesis.hardwareId');

                              var hardwareIdQuery;
                              if ((hardwareId.startsWith('/') && hardwareId.endsWith('/'))) {
                              hardwareIdQuery = "hardwareId =~ " + hardwareId;
                              } else {
                              hardwareIdQuery = "hardwareId = '" + hardwareId + "'";
                              }

                              return queryTemplate.replace("$TAGS",
                              hardwareIdQuery + " and type = '" + flowFile.getAttribute('esthesis.type') + "'");
                              }

                              function getCalculateFields(flowFile, operation) {
                              // Create the fields template (i.e. first part of the SELECT query).
                              var fieldsTemplate;
                              var fields = flowFile.getAttribute('esthesis.param.fields');
                              if (!fields) {
                              fieldsTemplate = operation + "(*)";
                              } else {
                              fieldsTemplate = operation + "(" + fields + ") as " +  operation + "_" + fields;
                              }

                              return fieldsTemplate;
                              }

                              /**
                              * Create a generic query request.
                              * @param flowFile The FlowFile to process information to create the request.
                              * @param fields A list of fields already resolved in a previous step.
                              */
                              function createQueryRequest(flowFile, fields) {
                              // Set the template for this type of execution.
                              var queryTemplate = "SELECT $FIELDS FROM $MEASUREMENT WHERE $TAGS $TIME ORDER BY time $ORDER $PAGING";

                              // Set fields, measurement and tags.
                              if (fields) {
                              queryTemplate = queryTemplate.replace("$FIELDS", fields);
                              }
                              else {
                              queryTemplate = _updateFields(flowFile, queryTemplate);
                              }
                              queryTemplate = _updateMeasurement(flowFile, queryTemplate);
                              queryTemplate = _updateTags(flowFile, queryTemplate);

                              // Set time (incoming time is in msec, so it needs to be converted to nanoseconds for InfluxDB).
                              var timeFrom = flowFile.getAttribute('esthesis.param.from');
                              var timeTo = flowFile.getAttribute('esthesis.param.to');
                              if (timeFrom && timeTo) {
                              timeFrom = timeFrom * 1000000;
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME",
                              "and time >= " + timeFrom + " and time <= " + timeTo);
                              } else if (timeFrom) {
                              timeFrom = timeFrom * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and time >= " + timeFrom);
                              } else if (timeTo) {
                              timeTo = timeTo * 1000000;
                              queryTemplate = queryTemplate.replace("$TIME", "and time <= " + timeTo);
                              } else {
                              queryTemplate = queryTemplate.replace("$TIME", "");
                              }

                              // Set order.
                              var order = flowFile.getAttribute('esthesis.param.order');
                              if (order) {
                              order = order.toLowerCase();
                              if ((order === 'asc' || order === 'desc')) {
                              queryTemplate = queryTemplate.replace("$ORDER", order);
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }
                              } else if (isMetadataQuery) {
                              queryTemplate = queryTemplate.replace("$ORDER", "desc");
                              } else {
                              queryTemplate = queryTemplate.replace("$ORDER", "asc");
                              }

                              // Set paging.
                              var limit = isMetadataQuery ? 1 : flowFile.getAttribute(
                              'http.query.param.limit');
                              var offset = flowFile.getAttribute('http.query.param.offset');
                              if (limit && offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit + " OFFSET " + offset);
                              } else if (limit) {
                              queryTemplate = queryTemplate.replace("$PAGING", "LIMIT " + limit);
                              } else if (offset) {
                              queryTemplate = queryTemplate.replace("$PAGING", "OFFSET " + offset);
                              } else {
                              queryTemplate = queryTemplate.replace("$PAGING", "");
                              }

                              log.info("Created InfluxDB query: " + queryTemplate);
                              return queryTemplate;
                              }

                              // Get references the the output stream to write results and to the incoming FlowFile.
                              var OutputStreamCallback = Java.type("org.apache.nifi.processor.io.OutputStreamCallback");
                              var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");
                              var flowFile = session.get();
                              var isMetadataQuery = flowFile.getAttribute('esthesis.type') === "metadata";

                              // If FlowFile exists proceed with processing.
                              if (flowFile != null) {
                              try {
                              // Define the query to be executed.
                              var queryTemplate;

                              // Call the appropriate handler for the the type of query requested.
                              var operation = flowFile.getAttribute('esthesis.operation').trim().toLowerCase();
                              var fields = flowFile.getAttribute('esthesis.param.fields');

                              if (isMetadataQuery && operation !== 'query' ) {
                              throw('Requested operation \'' + operation + "\' is not supported for metadata");
                              }

                              if (allowedOperations.indexOf(operation) > -1) {
                              queryTemplate = operation === 'query' ? createQueryRequest(flowFile) : createQueryRequest(
                              flowFile, getCalculateFields(flowFile, operation));
                              } else {
                              throw('Requested operation \'' + operation + "\' is not supported.");
                              }

                              // Replace FlowFile's content with the query to be executed.
                              flowFile = session.write(flowFile, new OutputStreamCallback(function (outputStream) {
                              outputStream.write(queryTemplate.getBytes(StandardCharsets.UTF_8))
                              }));

                              session.transfer(flowFile, REL_SUCCESS);
                              } catch (e) {
                              log.error(e);
                              session.transfer(flowFile, REL_FAILURE);
                              }
                              }

