        "v": {
          "description": "The set of value of the measurement",
          "anyOf": [
            {
              "not": {
                "type": "null"
              }
            },
            {
              "type": "object",
              "additionalProperties": {
                "not": {
                  "type": "null"
                }
              }
            }
          ]
        },
        "t": {
          "type": "integer",
          "minimum": 0,
          "description": "The EPOCH time this set of measurements took place in milliseconds"
        }
      },
      "required": [
        "m",
        "v"
      ]
    }
  },
  "anyOf": [
    {
      "type": "array",
      "items": {
        "$ref": "#/definitions/esthesis_data"
      }
    },
    {
      "$ref": "#/definitions/esthesis_data"
    }
  ]
